sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if(newValue == '')
	   {
		   return;
	   }
	var formField = 'u_what_needed';
	var whatneeded = g_form.getValue(formField);

	// clear all the choices from What needed list
	g_form.clearOptions(formField);

	// if the value of the Reqeust type field is hr, add HR choices and other
	switch(newValue)
	{
		case 'hr':
			g_form.addOption(formField, 'hr1', 'Human Resources 1');
			g_form.addOption(formField, 'hr2', 'Human Resources 2');
			break;
		case 'facilities':
			g_form.addOption(formField, 'facilities1', 'Facilities 1');
			g_form.addOption(formField, 'facilities2', 'Facilities 2');
			break;
		case 'legal':
			g_form.addOption(formField, 'legal1', 'Legal 1');
			g_form.addOption(formField, 'legal2', 'Legal 2');
			break;
	}
	g_form.addOption(formField, 'other', 'Other');

	if(isLoading && false == g_form.isNewRecord())
		{
			g_form.setValue(formField, whatneeded);
		}

}
